def get_mac(string):

    return "%X:%X:%X:%X:%X:%X" %(ord(string[0]),
                                 ord(string[1]),
                                 ord(string[2]),
                                 ord(string[3]),
                                 ord(string[4]),
                                 ord(string[5]))

def get_ip(string):

    return "%d.%d.%d.%d"%(ord(string[0]),
                          ord(string[1]),
                          ord(string[2]),
                          ord(string[3]))

def extract_tuples(reader):

    rtn_list = []
    other_list = []
    for (ts,pkt) in reader:
        eth = dpkt.ethernet.Ethernet(pkt)

        mac_src = get_mac(eth['src'])
        mac_dst = eth['dst']

        eth_data = eth['data']

        # Only parse IPv4
        if eth.type != 2048:
            other_list.append(eth)
            continue

        ip = eth.data

        ip_src = get_ip(ip['src'])
        ip_dst = get_ip(ip['dst'])
        # Is this TCP?
        if ip.p == 0x6:
            tcp = ip.data
            tcp_sport = tcp['sport']
            tcp_dport = tcp['dport']

            logger.debug("TCP: %s:%d -> %s:%d"%(ip_src,
                                                tcp_sport,
                                                ip_dst,
                                                tcp_dport))

            rtn_list.append({'protocol':'TCP',
                             'ip_src': ip_src,
                             'ip_dst': ip_dst,
                             'port_src': tcp_sport,
                             'port_dst': tcp_dport})
        # UDP
        elif ip.p == 0x11:
            udp = ip.data
            udp_sport = udp['sport']
            udp_dport = udp['dport']

            dns_hosts = []
            if udp_sport == '53' or udp_dport == 53:
                dns = dpkt.dns.DNS(udp.data)
                for q in dns['qd']:
                    dns_hosts.append(q['name'])

            logger.debug("UDP: %s:%d -> %s:%d"%(ip_src,
                                                udp_sport,
                                                ip_dst,
                                                udp_dport))

            rtn_list.append({'protocol':'UDP',
                             'ip_src': ip_src,
                             'ip_dst': ip_dst,
                             'port_src': udp_sport,
                             'port_dst': udp_dport,
                             'dns':dns_hosts})

    return rtn_list, other_list